package Operators_And_Variables;
import java.util.*;
public class While_Loop_Questions_Demo_Set_2 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		int i = 1;
		while (i<=n) {
			System.out.println("Hello");
			i=i+1;
		}
	}

}
